def get_verse_location(verse_number):
    """Given a verse number, return the book, chapter, and verse."""
    if not 1 <= verse_number <= TOTAL_VERSES:
        raise ValueError(f"Verse number must be between 1 and {TOTAL_VERSES}")
    
    for book, chapters in BIBLE_VERSE_INDEX.items():
        for chapter, range_info in chapters.items():
            if range_info["start"] <= verse_number <= range_info["end"]:
                verse_in_chapter = verse_number - range_info["start"] + 1
                return book, chapter, verse_in_chapter
    raise ValueError("Verse number not found in index")

def get_verse_range(book, chapter):
    """Given a book and chapter, return the start and end verse numbers."""
    book = book.lower()
    if book not in BIBLE_VERSE_INDEX:
        raise ValueError(f"Book '{book}' not found in index")
    if chapter not in BIBLE_VERSE_INDEX[book]:
        raise ValueError(f"Chapter {chapter} not found in {book}")
    
    return BIBLE_VERSE_INDEX[book][chapter]["start"], BIBLE_VERSE_INDEX[book][chapter]["end"]

