    verse_num = 35173  # Last verse: 2 Maccabees 15:39
    book, chapter, verse = get_verse_location(verse_num)
    print(f"Verse {verse_num} is {book.capitalize()} {chapter}:{verse}")
    
    start, end = get_verse_range("genesis", 1)
    print(f"Genesis 1 ranges from verse {start} to {end}")